exports.startsWith = function(string, value) {
	if (value.length == 1) {
		return string[0] == value;
	}
	
	var pattern = "^" + value;
	var regexp = new RegExp(pattern, 'g');
	return regexp.test(string);
};

exports.endsWith = function(string, value) {
	if (value.length == 1) {
		return string[string.length - 1] == value;
	}
	
	var pattern = value + "$";
	var regexp = new RegExp(pattern, 'g');
	return regexp.test(string);
};

exports.trim = function(string, character) {
	if (!character)
	{
		return string.trim();
	}
	else
	{
		var pattern = "^(" + character + "+)|(" + character + "+)$";
		var regexp = new RegExp(pattern, "g");
		return string.replace(regexp, "");
	}
};

exports.ucfirst = function(string)
{
	if (string.length < 1)
	{
		return string;
	}
	
	var firstChar = string.charAt(0);
	string = string.toLowerCase();
	string = firstChar.toUpperCase() + string.substr(1);
	
	return string;
};

exports.ucwords = function(string)
{
	var words = string.split(" ");
	var result = [];
	words.forEach(function(word)
	{
		result.push(exports.ucfirst(word));
	});
	
	return result.join(" ");
};