exports.startsWith = function(string, value) {
	if (value.length == 1) {
		return string[0] == value;
	}
	
	var pattern = "^" + value;
	var regexp = new RegExp(pattern, 'g');
	return regexp.test(string);
};

String.prototype.startsWith = function(value) {
	return exports.startWith(this, value);
};

exports.endsWith = function(string, value) {
	if (value.length == 1) {
		return string[string.length - 1] == value;
	}
	
	var pattern = value + "$";
	var regexp = new RegExp(pattern, 'g');
	return regexp.test(string);
};

String.prototype.endsWith = function(value) {
	return exports.endsWith(this, value);
};

exports.trimWith = function(string, character) {
	if (!character) {
		return string.trim();
	}
	else {
		var pattern = "^(" + character + "+)|(" + character + "+)$";
		var regexp = new RegExp(pattern, "g");
		return string.replace(regexp, "");
	}
};

String.prototype.trimWith = function(character) {
	return exports.trimWith(this, character);
};

exports.ucfirst = function(string) {
	if (string.length < 1) {
		return string;
	}
	
	var firstChar = string.charAt(0);
	string = string.toLowerCase();
	string = firstChar.toUpperCase() + string.substr(1);
	
	return string;
};

String.prototype.ucfirst = function() {
	return exports.ucfirst(this);
};

exports.ucwords = function(string) {
	var words = string.split(" ");
	var result = [];
	words.forEach(function(word) {
		result.push(exports.ucfirst(word));
	});
	
	return result.join(" ");
};

String.prototype.ucwords = function() {
	return exports.ucwords(this);
};